import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const Chat = () => {
    const [socket, setSocket] = useState(null);
    const [messages, setMessages] = useState([]);
    const [inputMessage, setInputMessage] = useState('');

    useEffect(() => {
        const newSocket = io();
        setSocket(newSocket);

        newSocket.on('message', (data) => {
            setMessages([...messages, data]);
        });

        return () => newSocket.close();
    }, []);

    const sendMessage = () => {
        if (inputMessage.trim() === '') return;

        socket.emit('message', { message: inputMessage });
        setInputMessage('');
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'column', height: '100vh', padding: '20px' }}>
            <div style={{ flex: 1, overflowY: 'auto' }}>
                {messages.map((msg, index) => (
                    <div key={index} style={{ marginBottom: '10px', padding: '10px', borderRadius: '5px', backgroundColor: '#f0f0f0' }}>
                        {msg}
                    </div>
                ))}
            </div>
            <div style={{ display: 'flex', marginTop: '20px' }}>
                <input
                    type="text"
                    value={inputMessage}
                    onChange={(e) => setInputMessage(e.target.value)}
                    placeholder="Type your message..."
                    style={{ flex: 1, padding: '10px', borderRadius: '5px', border: '1px solid #ccc', marginRight: '10px' }}
                />
                <button onClick={sendMessage} style={{ padding: '10px 20px', borderRadius: '5px', backgroundColor: '#007bff', color: '#fff', border: 'none', cursor: 'pointer', transition: 'background-color 0.3s' }}>
                    Send
                </button>
            </div>
        </div>
    );
};

export default Chat;
