import React, { useEffect, useRef,use } from 'react';
import io from 'socket.io-client';
import { useNavigate } from "react-router-dom";
import Cookies from "universal-cookie";
import Header from "./Header";

const bu = process.env.REACT_APP_BASEURL;

const Adminchat = () => {
  const navigate = useNavigate();
  const cookies = new Cookies();
  const [data, setUserData, ref] = useState([]);
  const socket = useRef(null);

  const token = cookies.get("jwtoken");

  const login = async () => {
    try {
      const res = await fetch(`${bu}/afterlogin`, {
        method: "GET",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
      });

      if (res.status === 401) {
        const error = new Error(res.error);
        throw error;
      }

      const data = await res.json();
      setUserData(data);
      console.log(data);
    } catch (err) {
      navigate("/login");
    }
  };

  useEffect(() => {
    login();

    // Initialize WebSocket connection
    socket.current = io('http://localhost:5000');

    // Listen for user connected event
    socket.current.on('userConnected', () => {
      console.log('A user is connected');
      // Add code to display a notification to the admin interface
      // For example, you can show a notification using a toast or modal
    });

    return () => {
      // Clean up WebSocket connection
      socket.current.disconnect();
    };
  }, []);

  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      marginTop: '20px',
    },
    messages: {
      border: '1px solid black',
      height: '300px',
      width: '100%',
      maxWidth: '600px',
      overflowY: 'scroll',
      padding: '10px',
      marginBottom: '10px',
      backgroundColor: '#f9f9f9',
    },
    message: {
      marginBottom: '5px',
    },
    inputContainer: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      width: '100%',
      maxWidth: '600px',
    },
    input: {
      width: '80%',
      padding: '10px',
      border: '1px solid #ccc',
      borderRadius: '4px',
      marginRight: '10px',
    },
    button: {
      padding: '10px 20px',
      border: 'none',
      backgroundColor: '#007bff',
      color: 'white',
      borderRadius: '4px',
      cursor: 'pointer',
    },
    buttonHover: {
      backgroundColor: '#0056b3',
    },
  };

  return (
    <>
      {ref.current.usertype === "1" && navigate("/login")}
      <Header />
      <div style={styles.container}>
        <h2>Welcome Admin, you are now connected to the chat</h2>
        <div style={styles.messages}></div>
      </div>
    </>
  );
};

export default Adminchat;
