// src/Chat.js

import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import { useNavigate } from "react-router-dom";
import Cookies from "universal-cookie";
import Header from "./Header";
const bu=process.env.REACT_APP_BASEURL
const Chat = () => {



    const navigate = useNavigate();
	const cookies = new Cookies();

	const [data,setuserdata,ref]=useState([]);
	const token = cookies.get("jwtoken");
  const login = async () => {

    try {
      const res = await fetch(`${bu}/afterlogin`, {
        method: "GET",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        
      });
     
      if (res.status === 401) {
      
        const error = new Error(res.error);
        throw error;
      }
      const data = await res.json();
     setuserdata(data)
     
     console.log(data)
      
    } catch (err) {
      navigate("/login");
      
     
      
    }
  };

  useEffect(() => {
    login();
    
  }, []);


  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const socket = useRef(null);

  useEffect(() => {
    socket.current = io('http://localhost:5000');

    socket.current.on('connect', () => {
      console.log('Socket.IO connected');
    });

    socket.current.on('message', (message) => {
      setMessages((prevMessages) => [...prevMessages, { content: message, timestamp: new Date() }]);
    });

    socket.current.on('disconnect', () => {
      console.log('Socket.IO disconnected');
    });

    return () => {
      socket.current.disconnect();
    };
  }, []);

  const sendMessage = () => {
    if (input.trim()) {
      const message = input;
      socket.current.emit('message', { message });
      setMessages((prevMessages) => [...prevMessages, { content: message, timestamp: new Date() }]);
      setInput('');
    }
  };

  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      marginTop: '20px',
    },
    messages: {
      border: '1px solid black',
      height: '300px',
      width: '100%',
      maxWidth: '600px',
      overflowY: 'scroll',
      padding: '10px',
      marginBottom: '10px',
      backgroundColor: '#f9f9f9',
    },
    message: {
      marginBottom: '5px',
    },
    inputContainer: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      width: '100%',
      maxWidth: '600px',
    },
    input: {
      width: '80%',
      padding: '10px',
      border: '1px solid #ccc',
      borderRadius: '4px',
      marginRight: '10px',
    },
    button: {
      padding: '10px 20px',
      border: 'none',
      backgroundColor: '#007bff',
      color: 'white',
      borderRadius: '4px',
      cursor: 'pointer',
    },
    buttonHover: {
      backgroundColor: '#0056b3',
    },
  };

  return (
    <>
    {ref.current.usertype==="1"&&navigate("/login")}
			<Header/>
    <div style={styles.container}>
      <h2>You are to {data.name} </h2>
      <div style={styles.messages}>
        {messages.map((message, index) => (
          <div key={index} style={styles.message}>
         
            <strong>{new Date(message.timestamp).toLocaleTimeString()}:</strong> {message.content}
          </div>
        ))}
      </div>
      <div style={styles.inputContainer}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
          style={styles.input}
        />
        <button
          onClick={sendMessage}
          style={styles.button}
          onMouseOver={(e) => (e.currentTarget.style.backgroundColor = styles.buttonHover.backgroundColor)}
          onMouseOut={(e) => (e.currentTarget.style.backgroundColor = styles.button.backgroundColor)}
        >
          Send
        </button>
      </div>
    </div>
    </>
  );
};

export default Chat;
