import React, { useEffect, useRef } from 'react';
import useState from 'react-usestateref';
import io from 'socket.io-client';
import { useNavigate } from 'react-router-dom';
import Cookies from 'universal-cookie';
import Header from '../Header';

const bu = process.env.REACT_APP_BASEURL;

const Adminchat = () => {
    const navigate = useNavigate();
    const cookies = new Cookies();

    const [data, setUserData, ref] = useState([]);
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    
    const token = cookies.get('jwtoken');

    const login = async () => {
        try {
            const res = await fetch(`${bu}/afterlogin`, {
                method: 'GET',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${token}`,
                },
            });
            if (res.status === 401) {
                const error = new Error(res.error);
                throw error;
            }
            const userData = await res.json();
            setUserData(userData);
            console.log(userData);
        } catch (err) {
            navigate('/login');
        }
    };

    useEffect(() => {
        login();
    }, []);

    const socket = useRef(null);

    useEffect(() => {
        socket.current = io('http://localhost:5000');

        socket.current.on('connect', () => {
            console.log('Socket.IO connected');
        });

        socket.current.on('message', (message) => {
            setMessages((prevMessages) => [...prevMessages, { content: message.content, sender: message.sender }]);
        });

        socket.current.on('disconnect', () => {
            console.log('Socket.IO disconnected');
        });

        return () => {
            socket.current.disconnect();
        };
    }, []);

    const sendMessage = () => {
        if (input.trim()) {
            const message = {
                content: input,
                sender: ref.current.name // Include the user's name here
            };
        
            socket.current.emit('message', { message });
            setInput('');
        }
    };

    const styles = {
        // Styles object remains unchanged
    };

    return (
        <>
            {ref.current.usertype === '2' && navigate('/login')}
            <Header />
            <div style={styles.container}>
                <h2>You are logged in as admin </h2>
                <div style={styles.messages}>
                    {messages.map((message, index) => (
                        <div key={index} style={styles.message}>
                            <strong>{message.sender}: </strong> {message.content}
                        </div>
                    ))}
                </div>
                <div style={styles.inputContainer}>
                    <input
                        type="text"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
                        style={styles.input}
                    />
                    <button
                        onClick={sendMessage}
                        style={styles.button}
                        onMouseOver={(e) => (e.currentTarget.style.backgroundColor = styles.buttonHover.backgroundColor)}
                        onMouseOut={(e) => (e.currentTarget.style.backgroundColor = styles.button.backgroundColor)}
                    >
                        Send
                    </button>
                </div>
            </div>
        </>
    );
};

export default Adminchat;
